mapper.insert:
INSERT INTO tb_member (
                        user_id
                      , password
                      )
               VALUES (
                         #{userId}
                       , #{password}
                       )

    SELECT *
      FROM tb_member
     WHERE 1=1
       AND user_id = #{userId}

