mapper.delete:
DELETE FROM tb_comment
 WHERE 1=1
   AND comment_no = #{commentNo}

mapper.insert:
INSERT INTO tb_comment (
                         post_no
                       , member_no
                       , content
                       )
                VALUES (  #{postNo}
                        , #{memberNo}
                        , #{content}
                        )

    SELECT *
      FROM tb_comment
     WHERE 1=1
       AND comment_no = LAST_INSERT_ID()

mapper.update:
UPDATE tb_comment
   SET content = #{content}
     , upd_dt = now()
 WHERE 1=1
   AND comment_no = #{commentNo}

    SELECT *
      FROM tb_comment
     WHERE 1=1
       AND comment_no = #{commentNo}
