mapper.delete:
DELETE FROM tb_post
 WHERE 1=1
   AND post_no = #{postNo}

mapper.insert:
INSERT INTO tb_post (
                       board_no
                     , member_no
                     , title
                     , content
                     )
              VALUES (  #{boardNo}
                      , #{memberNo}
                      , #{title}
                      , #{content}
                     )

    SELECT *
      FROM tb_post
     WHERE 1=1
       AND post_no = LAST_INSERT_ID()

mapper.update:
UPDATE tb_post
   SET title = #{title}
     , content = #{content}
     , upd_dt = now()
 WHERE 1=1
   AND post_no = #{postNo}

    SELECT *
      FROM tb_post
     WHERE 1=1
       AND post_no = #{postNo}
