mapper.select:
select count(board_cd)
from tb_board
where board_cd = #{boardName}

mapper.delete:
delete from tb_board
where board_cd = #{boardCd}
  and board_no = #{boardNo}

mapper.update:
update tb_board
set title = #{title}
    , content = #{content}
    , upd_dt = now()
where board_cd = #{boardCd}
  and board_no = #{boardNo}

    select * from tb_board where board_no = #{boardNo}
